mapper.select:
SELECT COUNT(*) as memberCount FROM MEMBER WHERE
member_insert_date BETWEEN #{startDate}
AND date_format(date(#{endDate}), '%Y-%m-%d')

mapper.update:
UPDATE MEMBER
SET member_state = '회원'
WHERE member_id = #{member}

UPDATE MEMBER
SET member_state = '관리자'
WHERE member_id = #{admin}
