mapper.insert:
    		INSERT 
    		INTO MY_ADDRESS (member_id, recipient_name, recipient_phone_num, address1, address2, address_main, postcode)
				VALUES (
		            #{member_id}
		            , #{member_name}
		            , #{member_phone}
		            , #{member_address1}
		            , #{member_address2}
		            , 0
		            , #{zonecode}
				)

mapper.update:
UPDATE MY_ADDRESS
SET address_main = '0'
WHERE address_main = '1' AND member_id = #{member_id};

